    if (this.verificationResults[jobId]?.result) {
      if (this.cardAnimationState[jobId]) {
        this.cardAnimationState[jobId] = 'pulse';
        setTimeout(() => {
          if (this.cardAnimationState[jobId]) {
            this.cardAnimationState[jobId] = 'visible';
          }
        }, 600); // Match the pulse animation duration
      }
    }

    // Set verifying state
    this.verificationResults[jobId] = { isVerifying: true };

    // Simulate verification delay
    setTimeout(() => {
      // For demo purposes, randomly decide if job is fake or not
      const isFake = Math.random() > 0.7;

      this.verificationResults[jobId] = { 
        isVerifying: false, 
        result: {
          isFake: isFake,
          message: isFake ? 'This job listing has suspicious elements' : 'This job appears to be legitimate',
          details: isFake ? 
            [
              'Salary is significantly above market rate',
              'Company contact information is incomplete',
              'Job description contains vague responsibilities'
            ] : 
            [
              'Company has a verified online presence',
              'Salary range is appropriate for the position',
              'Job requirements are reasonable for the role'
            ]
        }
      };

      // Trigger pulse animation after verification
      if (this.cardAnimationState[jobId]) {
        this.cardAnimationState[jobId] = 'pulse';
        setTimeout(() => {
          if (this.cardAnimationState[jobId]) {
            this.cardAnimationState[jobId] = 'visible';
          }
        }, 600); // Match the pulse animation duration
      }
    }, 2000); // Simulate verification delay
  }

  truncateText(text: string, maxLength: number): string {
    if (text.length <= maxLength) {
      return text;
    }
    return text.substring(0, maxLength) + '...';
  }

  onCardClick(event: MouseEvent, jobId?: string): void {
    // Don't trigger for clicks on buttons or links inside the card
    if (
      event.target instanceof HTMLElement && 
      (event.target.tagName === 'BUTTON' || 
       event.target.tagName === 'A' ||
       event.target.closest('button') ||
       event.target.closest('a'))
    ) {
      return;
    }

    // If job has not been verified yet, verify it
    if (jobId && !this.verificationResults[jobId]) {
      this.verifyJob(jobId);
    }
  }

  // Add missing expandDescription method
  expandDescription(jobId: string): void {
    // This method would typically open a modal or expand the job details
    console.log('Expanding description for job:', jobId);

    // Find the job
    const job = this.jobs.find(j => j.id === jobId);
    if (job) {
      // For demo purposes, just toggle a verification if not already done
      if (!this.verificationResults[jobId]) {
        this.verifyJob(jobId);
      }
    }
  }
