import { Component, OnInit } from '@angular/core';
import { JobService, Job, JobVerificationResult } from '../../services/job.service';
import { finalize } from 'rxjs/operators';

@Component({
  selector: 'app-job-listings',
  templateUrl: './job-listings.component.html',
  styleUrls: ['./job-listings.component.css']
})
export class JobListingsComponent implements OnInit {
  jobs: Job[] = [];
  isLoading = true;
  error: string | null = null;
  verificationResults: { [key: string]: { isVerifying: boolean, result?: JobVerificationResult } } = {};
  filteredJobs: Job[] = [];
  searchTerm = '';
  selectedFilter = 'all';
  sortOption = 'newest';

  // Animation states
  cardAnimationState: { [key: string]: string } = {};

  constructor(private jobService: JobService) { }

  ngOnInit(): void {
    this.loadJobs();
  }

  loadJobs(): void {
    this.isLoading = true;
    this.error = null;

    this.jobService.getJobs()
      .pipe(
        finalize(() => this.isLoading = false)
      )
      .subscribe({
        next: (jobs) => {
          this.jobs = jobs;
          this.filteredJobs = [...jobs];

          // Initialize animation states
          jobs.forEach(job => {
            if (job.id) {
              this.cardAnimationState[job.id] = 'initial';
              setTimeout(() => {
                if (job.id) this.cardAnimationState[job.id] = 'visible';
              }, 100 + (jobs.indexOf(job) * 50)); // Staggered animation
            }
          });
        },
        error: (err) => {
          console.error('Error loading jobs:', err);
          this.error = 'Failed to load jobs. Please try again later.';
        }
      });
  }

  verifyJob(jobId: string): void {
    if (!jobId) return;

    // Set verifying state
    this.verificationResults[jobId] = { isVerifying: true };

    this.jobService.verifyJobById(jobId)
      .subscribe({
        next: (result) => {
          this.verificationResults[jobId] = { 
            isVerifying: false, 
            result 
          };

          // Add animation for result
          if (jobId) {
            this.cardAnimationState[jobId] = 'pulse';
            setTimeout(() => {
              if (jobId) this.cardAnimationState[jobId] = 'visible';
            }, 600);
          }
        },
        error: (err) => {
          console.error('Error verifying job:', err);
          this.verificationResults[jobId] = { 
            isVerifying: false, 
            result: { 
              isFake: true, 
              message: 'Error verifying job. Please try again.' 
            } 
          };
        }
      });
  }

  applyFilters(): void {
    let filtered = [...this.jobs];

    // Apply search term filter
    if (this.searchTerm) {
      const term = this.searchTerm.toLowerCase();
      filtered = filtered.filter(job => 
        job.title.toLowerCase().includes(term) || 
        job.location.toLowerCase().includes(term) ||
        job.companyProfile.toLowerCase().includes(term) ||
        job.industry.toLowerCase().includes(term)
      );
    }

    // Apply category filter
    if (this.selectedFilter !== 'all') {
      filtered = filtered.filter(job => job.industry === this.selectedFilter);
    }

    // Apply sorting
    if (this.sortOption === 'newest') {
      // Assuming there's a createdAt field or using id as proxy for recency
      filtered = filtered.sort((a, b) => {
        return (b.id || '').localeCompare(a.id || '');
      });
    } else if (this.sortOption === 'title') {
      filtered = filtered.sort((a, b) => a.title.localeCompare(b.title));
    }

    this.filteredJobs = filtered;
  }

  resetFilters(): void {
    this.searchTerm = '';
    this.selectedFilter = 'all';
    this.sortOption = 'newest';
    this.filteredJobs = [...this.jobs];
  }

  getIndustryCategories(): string[] {
    // Extract unique industry values from jobs
    const industries = new Set<string>();
    this.jobs.forEach(job => {
      if (job.industry) industries.add(job.industry);
    });
    return Array.from(industries);
  }

  // Helper method to truncate text
  truncateText(text: string, maxLength: number): string {
    if (!text) return '';
    return text.length > maxLength ? text.substring(0, maxLength) + '...' : text;
  }
}
