import { Component, OnInit } from '@angular/core';
import { JobService } from '../../services/job.service';
import { Job, JobVerificationResult } from '../../models/job.model';

@Component({
  selector: 'app-job-listings',
  templateUrl: './job-listings.component.html',
  styleUrls: ['./job-listings.component.css']
})
export class JobListingsComponent implements OnInit {
  jobs: Job[] = [];
  filteredJobs: Job[] = [];
  isLoading: boolean = true;
  error: string | null = null;
  searchTerm: string = '';
  selectedFilter: string = 'all';
  sortOption: string = 'newest';
  verificationResults: { [key: string]: { isVerifying: boolean, result?: JobVerificationResult } } = {};
  cardAnimationState: { [key: string]: 'initial' | 'visible' | 'pulse' } = {};
  animationDelayIncrement: number = 100; // ms between each card animation

  constructor(private jobService: JobService) { }

  ngOnInit(): void {
    this.loadJobs();
  }

  loadJobs(): void {
    this.isLoading = true;
    this.error = null;

    this.jobService.getJobs().subscribe({
      next: (jobs) => {
        this.jobs = jobs;
        this.applyFilters();
        this.isLoading = false;

        // Initialize animation states for all jobs
        setTimeout(() => {
          this.jobs.forEach((job, index) => {
            if (job.id) {
              // Set initial state
              this.cardAnimationState[job.id] = 'initial';

              // Stagger the animations
              setTimeout(() => {
                if (job.id) {
                  this.cardAnimationState[job.id] = 'visible';
                }
              }, index * this.animationDelayIncrement);
            }
          });
        }, 0);
      },
      error: (err) => {
        console.error('Error loading jobs:', err);
        this.error = 'Failed to load jobs. Please try again later.';
        this.isLoading = false;
      }
    });
  }

  applyFilters(): void {
    // Filter by search term and industry
    let filtered = this.jobs;

    // Apply search filter
    if (this.searchTerm.trim() !== '') {
      const searchTermLower = this.searchTerm.toLowerCase();
      filtered = filtered.filter(job => 
        job.title.toLowerCase().includes(searchTermLower) ||
        job.description.toLowerCase().includes(searchTermLower) ||
        job.companyProfile.toLowerCase().includes(searchTermLower) ||
        job.location.toLowerCase().includes(searchTermLower)
      );
    }

    // Apply industry filter
    if (this.selectedFilter !== 'all') {
      filtered = filtered.filter(job => job.industry === this.selectedFilter);
    }

    // Apply sorting
    if (this.sortOption === 'title') {
      filtered = filtered.sort((a, b) => a.title.localeCompare(b.title));
    } else if (this.sortOption === 'newest') {
      // Assuming jobs are already sorted by newest in the API response
      // If there's a date field, we could sort by that instead
    }

    this.filteredJobs = filtered;
  }

  resetFilters(): void {
    this.searchTerm = '';
    this.selectedFilter = 'all';
    this.sortOption = 'newest';
    this.applyFilters();
  }

  getIndustryCategories(): string[] {
    // Extract unique industry categories from jobs
    const industries = new Set<string>();
    this.jobs.forEach(job => {
      if (job.industry) {
        industries.add(job.industry);
      }
    });
    return Array.from(industries).sort();
  }

  verifyJob(jobId: string): void {
    // If already verified, trigger pulse animation
    if (this.verificationResults[jobId]?.result) {
      if (this.cardAnimationState[jobId]) {
        this.cardAnimationState[jobId] = 'pulse';
        setTimeout(() => {
          if (this.cardAnimationState[jobId]) {
            this.cardAnimationState[jobId] = 'visible';
          }
        }, 600); // Match the pulse animation duration
      }
    }

    // Set verifying state
    this.verificationResults[jobId] = { isVerifying: true };

    this.jobService.verifyJobById(jobId).subscribe({
      next: (result) => {
        this.verificationResults[jobId] = { 
          isVerifying: false, 
          result: result 
        };

        // Trigger pulse animation after verification
        if (this.cardAnimationState[jobId]) {
          this.cardAnimationState[jobId] = 'pulse';
          setTimeout(() => {
            if (this.cardAnimationState[jobId]) {
              this.cardAnimationState[jobId] = 'visible';
            }
          }, 600); // Match the pulse animation duration
        }
      },
      error: (err) => {
        console.error('Error verifying job:', err);
        this.verificationResults[jobId] = { 
          isVerifying: false, 
          result: {
            isFake: true,
            message: 'Error verifying job',
            details: ['An error occurred while trying to verify this job. Please try again later.']
          }
        };
      }
    });
  }

  truncateText(text: string, maxLength: number): string {
    if (text.length <= maxLength) {
      return text;
    }
    return text.substring(0, maxLength) + '...';
  }

  onCardClick(event: MouseEvent, jobId?: string): void {
    // Don't trigger for clicks on buttons or links inside the card
    if (
      event.target instanceof HTMLElement && 
      (event.target.tagName === 'BUTTON' || 
       event.target.tagName === 'A' ||
       event.target.closest('button') ||
       event.target.closest('a'))
    ) {
      return;
    }

    // If job has not been verified yet, verify it
    if (jobId && !this.verificationResults[jobId]) {
      this.verifyJob(jobId);
    }
    // If job has already been verified, trigger pulse animation
    else if (jobId && this.cardAnimationState[jobId]) {
      this.cardAnimationState[jobId] = 'pulse';
      setTimeout(() => {
        if (this.cardAnimationState[jobId]) {
          this.cardAnimationState[jobId] = 'visible';
        }
      }, 600);
    }
  }
}
